<?phpif (!(defined('IN_IA'))) {    exit('Access Denied');}/** * 圈子模型 * @author fish */class Quan_SnailFishShopModel{    public function get_memberid(){        global $_W;        global $_GPC;                $token =  $_GPC['token'];        $token_param = array();        $token_param[':uniacid'] = $_W['uniacid'];        $token_param[':token'] = $token;                $weprogram_token = pdo_fetch("select member_id from ".tablename('lionfish_comshop_weprogram_token')." where uniacid=:uniacid and token=:token ",$token_param);                if(  empty($weprogram_token) ||  empty($weprogram_token['member_id']) )        {            return false;            die();        }                return $member_id = $weprogram_token['member_id'];    }    /**    检测圈子是否存在     **/    public function check_quan($seller_id)    {		global $_W;        global $_GPC;		        $group_info = pdo_fetch("select * from ".tablename('lionfish_comshop_group')." where seller_id=:seller_id and uniacid=:uniacid ", 						array(':seller_id' => $seller_id, ':uniacid' => $_W['uniacid']) );        if (empty($group_info)) {                        $ins_data                 = array();            $ins_data['seller_id']    = $seller_id;            $ins_data['uniacid']    = $_W['uniacid'];            $ins_data['title']        = '';            $ins_data['post_count']   = 0;            $ins_data['status']       = 1;            $ins_data['member_count'] = 0;            $ins_data['create_time']  = time();            pdo_insert('lionfish_comshop_group', $ins_data);            $group_id                 = pdo_insertid();        } else {            $group_id = $group_info['id'];        }        return $group_id;    }    /**    用户加入圈子     **/    public function member_add_group($member_id, $group_id)    {        $group_member_data                = array();        $group_member_data['group_id']    = $group_id;        $group_member_data['member_id']   = $member_id;        $group_member_data['status']      = 1;        $group_member_data['last_view']   = time();        $group_member_data['position']    = 1;        $group_member_data['create_time'] = time();        pdo_insert('lionfish_comshop_group_member', $group_member_data);    }    /**    点赞/取消赞     **/    public function member_fav_post($member_id, $post_id)    {        $post_fav = pdo_fetch("select * from ".tablename('lionfish_comshop_group_post_fav')." where member_id=:member_id and post_id=:post_id", array(':member_id' => $member_id, ':post_id' => $post_id));        if (empty($post_fav)) {            $fav_data              = array();            $fav_data['member_id'] = $member_id;            $fav_data['post_id']   = $post_id;            $fav_data['fav_time']  = time();            pdo_insert('lionfish_comshop_group_post_fav', $fav_data);            $rs                    = pdo_insertid();            if ($rs) {                pdo_update('lionfish_comshop_group_post', array('fav_count +=' => 1), array('id' => $post_id));            }            return array('code' => 1, 'fav_id' => $rs);        } else {            $fav_info = pdo_fetch("select * from ".tablename('lionfish_comshop_group_post_fav')." where seller_id=:seller_id and post_id=:post_id", array(':member_id' => $member_id, ':post_id' => $post_id));            $fav_id   = $fav_info['id'];            pdo_delete('lionfish_comshop_group_post_fav', array('member_id' => $member_id, 'post_id' => $post_id));            pdo_update('lionfish_comshop_group_post', array('fav_count -=' => 1), array('id' => $post_id));            return array('code' => 2, 'fav_id' => $fav_id);        }    }    /**    评论     **/    public function comment_group_post($post_id, $content, $member_id, $to_member_id)    {        //group_lzl_reply        $reply_data                 = array();        $reply_data['post_id']      = $post_id;        $reply_data['content']      = htmlspecialchars($content);        $reply_data['member_id']    = $member_id;        $reply_data['to_member_id'] = $to_member_id;        $reply_data['status']       = 1;        $reply_data['create_time']  = time();        $rs                         = M('group_lzl_reply')->add($reply_data);        if ($rs) {            pdo_update('lionfish_comshop_group_post', array('reply_count -=' => 1), array('id' => $post_id));            pdo_update('lionfish_comshop_group_post', array('last_reply_time' => time()), array('id' => $post_id));        }        return $rs;    }    /**    发布     **/    public function send_group_post($data)    {        //group_post        $ins_data = array();        $ins_data['member_id'] = $data['member_id'];        $ins_data['uniacid'] = $data['uniacid'];        $ins_data['group_id'] = $data['group_id'];        $ins_data['goods_id'] = $data['goods_id'];        $ins_data['title'] = $data['title'];        $ins_data['content'] = $data['content'];        $ins_data['link'] = isset($data['link']) ? $data['link'] : '';        $ins_data['is_share'] = isset($data['is_share']) ? $data['is_share'] : 0;        $ins_data['status']    = 1;        $ins_data['is_vir']    = isset($data['is_vir']) ? $data['is_vir'] : 0;        $ins_data['avatar']    = isset($data['avatar']) ? $data['avatar'] : '';        $ins_data['user_name'] = isset($data['user_name']) ? $data['user_name'] : '';        $ins_data['last_reply_time'] = time();        $ins_data['fav_count'] = 0;        $ins_data['reply_count'] = 0;        $ins_data['create_time'] = time();        pdo_insert('lionfish_comshop_group_post',$ins_data);		$res = pdo_insertid();        return $res;    }    /**    加载圈子内容     **/    public function load_group_post($group_id, $post_id, $up_down, $limit = 10)    {        global $_W;        global $_GPC;        //up_down  1 底部加载， 2顶部加载。        $where = " and p.group_id = {$group_id} ";        $order_by = "";        if ($up_down == 1) {            if ($post_id > 0) {                $where .= " and p.id <{$post_id} ";            }            $order_by = " p.id desc ";        } else {            $where .= " and p.id >{$post_id} ";            $order_by = " p.id desc ";        }        $sql = "select p.*,m.username,m.avatar as avatar2 from " . tablename('lionfish_comshop_group_post') . " as p left join " . tablename('snailfish_member') . " as m on p.member_id = m.member_id where 1=1  {$where} order by {$order_by} limit {$limit} ";        $list = pdo_fetchall($sql);		        // $member_id = $this->get_memberid();        // if(!$member_id){        //     echo json_encode( array('code' => 1,'msg' => '您未登录') );        //     die();        // }        foreach ($list as $key => $val) {            // $has_fav = pdo_fetch("select * from ".tablename('lionfish_comshop_group_post_fav')." where uniacid=:uniacid and post_id=:post_id and :member_id=member_id ", array(':uniacid' => $_W['uniacid'], ':post_id' => $val['id'], ':member_id' => $member_id ));            $has_fav = pdo_fetch("select * from ".tablename('lionfish_comshop_group_post_fav')." where uniacid=:uniacid and post_id=:post_id", array(':uniacid' => $_W['uniacid'], ':post_id' => $val['id']));            if ($val['is_vir'] == 1) {               $val['uname'] = $val['user_name'];               $val['username'] = $val['user_name'];			   $val['avatar'] = tomedia($val['avatar']);            } else {                $val['avatar'] = $val['avatar2'];            }            $val['uname'] = $val['username'];            $val['has_fav'] = empty($has_fav) ? false : true;            $val['title'] = htmlspecialchars_decode($val['title']);            $val['content'] = unserialize($val['content']);            $image_list = explode(",", $val['content']);            foreach ($image_list as $kkk => $vvv) {                $vvv              = tomedia($vvv);                $image_list[$kkk] = $vvv;            }            $val['content'] = $image_list[0] !="" ? $image_list : "";            $val['fav_list'] = $this->get_post_fav_info($val['id']);            //获取评论信息            $val['comment_list'] = $this->get_post_comment_info($val['id']);            if ($val['goods_id'] != 0) {                $where = 'where id='.$val['goods_id'];                $val['goods_info'] = $this->get_goods_info($where);                $val['goods_info']['buy_user'] = $this->get_goods_pin_avatar($val['goods_id'], 5);            }            $val['last_reply_date'] = date('Y-m-d H:i:s', $val['last_reply_time']);            $list[$key] = $val;        }				        return $list;    }    /**     * 获取拼购人头像     * @return array     */    public function get_goods_pin_avatar($goods_id, $limit = 5)    {        $sql = "select distinct(m.member_id), m.avatar from " . tablename('lionfish_comshop_order_goods') . " as og ," . tablename('snailfish_order') . " as o," . tablename('snailfish_member') . " as m where og.order_id=o.order_id and o.member_id=m.member_id  and og.goods_id={$goods_id} order by o.order_id desc limit {$limit}";        $avatar_list = pdo_fetchall($sql);        if (empty($avatar_list) || count($avatar_list) < $limit) {            $del = $limit - count($avatar_list);            $list = pdo_fetchall("select * from ".tablename("lionfish_comshop_jiauser")." order by rand() limit {$del}");            foreach ($list as $val) {                $tmp = array();                $tmp['avatar'] = tomedia($val['avatar']);                $avatar_list[] = $tmp;            }        }        return $avatar_list;    }    /**    获取评论信息     **/    public function get_post_comment_info($post_id)    {                $sql = "select p.*,m.username,m.avatar from " . tablename('lionfish_comshop_group_lzl_reply') . " p," . tablename('snailfish_member') . " as m where p.member_id = m.member_id and p.post_id = {$post_id} order by id asc ";        $list = pdo_fetchall($sql);        foreach ($list as $key => $val) {            $val['content'] = htmlspecialchars_decode($val['content']);            if ($val['to_member_id'] > 0) {                $to_member_info = pdo_fetch("select username from ".tablename('lionfish_comshop_member')." where member_id={$val['to_member_id']}");                $val['to_member_name'] = $to_member_info['username'];            }            $list[$key] = $val;        }        return $list;    }    /**    获取点赞信息     **/    public function get_post_fav_info($post_id)    {        $sql = "select p.*,m.username,m.avatar from " . tablename('lionfish_comshop_group_post_fav') . " p," . tablename('snailfish_member') . " as m where p.member_id = m.member_id and p.post_id = {$post_id}";        $list = pdo_fetch($sql);        return $list;    }    /**    获取圈子信息     **/    public function get_quan_info($where = array())    {        $group_info = pdo_fetch("select * from ".tablename('lionfish_comshop_group').$where);        return $group_info;    }    /**    获取商品信息     **/    public function get_goods_info($where = array())    {        $goods_info = pdo_fetch("select id,seller_count,sales,goodsname from ".tablename('lionfish_comshop_goods').$where);        $good_image = load_model_class('pingoods')->get_goods_images($goods_info['id']);        if( !empty($good_image) )        {            $goods_info['image'] = tomedia($good_image['image']);        }        $price_arr = load_model_class('pingoods')->get_goods_price($goods_info['id']);        $goods_info['danprice'] = $price_arr['danprice'];        $goods_info['price'] = $price_arr['price'];        $goods_info['seller_count'] += $goods_info['sales'];        $goods_info['name'] = $goods_info['goodsname'];        return $goods_info;    }    /**    加载圈子内容     **/    public function load_dynamic_post($goods_id, $post_id, $up_down, $limit = 10, $is_goods_info = 1)    {        //up_down  1 底部加载， 2顶部加载。        $where = " and p.goods_id = {$goods_id} ";        $order_by = "";        if ($up_down == 1) {            if ($post_id > 0) {                $where .= " and p.id <{$post_id} ";            }            $order_by = " p.id desc ";        } else {            $where .= " and p.id >{$post_id} ";            $order_by = " p.id desc ";        }        $sql = "select p.*,m.username,m.avatar from " . tablename('lionfish_comshop_group_post') . " p," . tablename('snailfish_member') . " as m where p.member_id = m.member_id {$where} order by {$order_by} limit {$limit} ";        $list = pdo_fetchall($sql);        $member_id = $this->get_memberid();        if(!$member_id){            echo json_encode( array('code' => 0,'msg' => '您未登录') );            die();        }        foreach ($list as $key => $val) {            $has_fav = pdo_fetch("select * from ".tablename('lionfish_comshop_group_post_fav')."where post_id={$val['id']} and member_id={$member_id}");            $val['has_fav'] = empty($has_fav) ? false : true;            $val['title'] = htmlspecialchars_decode($val['title']);            $val['fav_list'] = $this->get_post_fav_info($val['id']);            if ($is_goods_info == 1) {                //获取评论信息                $val['comment_list'] = $this->get_post_comment_info($val['id']);                if ($val['goods_id'] != 0) {                    $where = 'where id='.$val['goods_id'];                    $val['goods_info'] = $this->get_goods_info($where);                }            }            $list[$key] = $val;        }        return $list;    }    /**    加载圈子内容     **/    public function load_dynamic_view($id, $post_id)    {        $where = " and p.id = {$id} ";        $order_by = "";        $sql = "select p.*,m.username,m.avatar from " . tablename('lionfish_comshop_group_post') . " p," . tablename('snailfish_member') . " as m where p.member_id = m.member_id {$where} limit 1";        $list = pdo_fetchall($sql);        $member_id = $this->get_memberid();        if(!$member_id){            echo json_encode( array('code' => 0,'msg' => '您未登录') );            die();        }        foreach ($list as $key => $val) {            $has_fav = pdo_fetch("select * from ".tablename('lionfish_comshop_group_post_fav')."where post_id={$val['id']} and member_id={$member_id}");            $val['has_fav'] = empty($has_fav) ? false : true;            $val['title'] = htmlspecialchars_decode($val['title']);            $val['fav_list'] = $this->get_post_fav_info($val['id']);            //获取评论信息            $val['comment_list'] = $this->get_post_comment_info($val['id']);            if ($val['goods_id'] != 0) {                $where = 'where id='.$val['goods_id'];                $val['goods_info'] = $this->get_goods_info($where);            }            $list[$key] = $val;        }        return $list;    }}